typedef struct Enemy {
    Vector2 position;
    float speed;
} Enemy;

Enemy enemies[3];

int GameOver(Vector2 playerPos, Enemy enemies[]) {
    for (int i = 0; i < numEnemiesTotal; i++) {
        float distance = Vector2Distance(playerPos, enemies[i].position);
        if (distance < 10) {
            
            return 1;
        }
    }
    return 0;
}

void moveEnemy(Enemy *current, Vector2 target) {
    float distX = target.x - current->position.x;
    float distY = target.y - current->position.y;
    
    if (fabsf(distX) > fabsf(distY)) {
        if (distX > 0) {
            current->position.x += current->speed;
        } else if (distX < 0) {
            current->position.x -= current->speed;
        }
    } else {
        if (distY > 0) {
            current->position.y += current->speed;
        } else if (distY < 0) {
            current->position.y -= current->speed;
        }
    }
}
    
void spawnEnemies(int numEnemies) {
    for (int i = 0; i < numEnemies; i++) {
        if (numEnemiesTotal >= 3) {
            return;
        }
        
        int enemyX, enemyY;
        do {
            enemyX = GetRandomValue(1, 68);
            enemyY = GetRandomValue(1, 107);
        } while (tileMap[enemyY][enemyX] != chao);
        
        enemies[numEnemiesTotal].position.x = enemyX * tileSize + tileSize / 2 - 20 / 2;
        enemies[numEnemiesTotal].position.y = enemyY * tileSize + tileSize / 2 - 20 / 2;
        enemies[numEnemiesTotal].speed = 1.0f + (numEnemiesTotal/2) + 0.5f;
        numEnemiesTotal++;
    }
}

void updateEnemies(Vector2 playerPos) {
    const float minDistance = 40.0f;
    for (int i = 0; i < 3; i++) {
        moveEnemy(&enemies[i], playerPos);
        for (int j = i + 1; j < 3; j++) {
            if (Vector2Distance(enemies[i].position, enemies[j].position) < minDistance) {
                Vector2 dir = Vector2Normalize(Vector2Subtract(enemies[i].position, enemies[j].position));
                enemies[i].position = Vector2Add(enemies[i].position, Vector2Scale(dir, 2.0f));
                enemies[j].position = Vector2Subtract(enemies[j].position, Vector2Scale(dir, 2.0f));
            }
        }
    }
}

