Vector2 arrowKeysMovement(Vector2 target, musicas musics) {
    Vector2 movement = {0.0f, 0.0f};
    Vector2 nextPosition = target;
	
    if (IsKeyDown(KEY_D)) {
        nextPosition.x += speedPlayer + numCollected;
        if (!checkCollision(nextPosition, musics)) {
            target = nextPosition;
        }
    }
    if (IsKeyDown(KEY_A)) {
        nextPosition.x -= speedPlayer + numCollected;
        if (!checkCollision(nextPosition, musics)) {
            target = nextPosition;
        }
    }
    if (IsKeyDown(KEY_W)) {
        nextPosition.y -= speedPlayer + numCollected;
        if (!checkCollision(nextPosition, musics)) {
            target = nextPosition;
        }
    }
    if (IsKeyDown(KEY_S)) {
        nextPosition.y += speedPlayer + numCollected;
        if (!checkCollision(nextPosition, musics)) {
            target = nextPosition;
        }
    }
    movement = Vector2Normalize(Vector2Subtract(target, nextPosition));
    movement = Vector2Scale(movement, 0.01f);
    target = Vector2Add(target, movement);
    return target;
}

